import React from "react";

class UserInput extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state = {value: ""};
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleSubmit()
    {
        console.log("Submitted");
    };

    render(){
        return(
            <div>
                <form>
                    <input></input>
                    <button onClick={this.handleSubmit}>
                        Input Command
                    </button>
                </form>
            </div>
        )
    };
};

export default UserInput;
